        setEmptyInput(true);
    };
    const sendToDo=(event)=>{
event.preventDefault();
    if(event.target.children[0].value==="")
        {
       setTrueHandler();        }
        else
        {     addToDo(toDoInput);
        setFalseHandler();
        }
    }; return(
        <>
